import { useSelector } from "react-redux";
import { RootState } from "../../store";
import { IProduct } from "../../types";
import { useDispatch } from "react-redux";
import { deleteProductAction, totalProductAction } from "../../actions/productsActions";
import { FC } from "react";
import { EditProduct } from "../index";

interface IProductList {
  isLoading: boolean
}

const ProductList: FC<IProductList> = () => {
  const dispatch = useDispatch();

  const products = useSelector<RootState, IProduct[]>(
    (state) => state.products.list
  );

  return (
    <div className="block-products">
      <h1>Продукты:</h1>

      <table>
        <thead>

          <tr>

            <th>
              №
            </th>

            <th>
              Имя
            </th>

            <th>
              Кол-во
            </th>

            <th>
              Закупочная цена
            </th>

            <th>
              Цена продажи
            </th>

            <th>
              Удалить
            </th>

          </tr>
        </thead>
        <tbody >
          {products.map((item) => (

            <tr key={item.id}>

              <td>
                {item.id}
              </td>

              <td>
                {item.name}
              </td>

              <td>
                {item.quantity}
              </td>

              <td>
                {item.purchasePrice}
              </td>

              <td>
                {item.sellingPrice}
              </td>
              <td>
                <div>
                  {/* @ts-ignore */}
                  <button className="btn-mini" onClick={() => { dispatch(deleteProductAction(item.id)) }}>X</button>
                </div>
              </td>

            </tr>
          ))}
          <tr>
            <th>
              Итого
            </th>
            <th>

            </th>
            <th>

            </th>
            <td>
            </td>
            <td>
            </td>
          </tr>
        </tbody>
      </table>
    </div>
  )
}

export default ProductList;
