import { useDispatch } from "react-redux";
import { createProductAction } from "../../actions/productsActions";
import { FormEvent, useState } from "react";

const AddProductForm = () => {
  
  const dispatch = useDispatch();
  const [name, setName] = useState("");
  const [purchasePrice, setPurchasePrice] = useState("");
  const [sellingPrice, setSellingPrice] = useState("");
  const [quantity, setQuantity] = useState("");

  const handleClick = (e: FormEvent) => {
    e.preventDefault();
    let products = JSON.parse(localStorage.getItem("products"));
    localStorage.setItem("products", JSON.stringify(dispatch(createProductAction({ name, quantity: parseInt(quantity), purchasePrice: parseInt(purchasePrice), sellingPrice: parseInt(sellingPrice),}))));
  };

  return (
    <div >
      <form className="block-add" onSubmit={handleClick}>
        <input
          placeholder="Имя"
          value={name}
          onChange={(e) => setName(e.target.value)}
        />
       
        <input
          placeholder="Кол-во"
          type="number"
          value={quantity}
          onChange={(e) => setQuantity(e.target.value)}
        />

        <input
          placeholder="Закупочная цена"
          value={purchasePrice}
          onChange={(e) => setPurchasePrice(e.target.value)}
        />

        <input
          placeholder="Цена продажи"
          value={sellingPrice}
          onChange={(e) => setSellingPrice(e.target.value)}
        />

        <button>Добавить</button>
      </form>
    </div>
  );
};

export default AddProductForm;
