import { useDispatch } from "react-redux";
import { createProductAction } from "../../actions/productsActions";
import { FormEvent, useState } from "react";
const initialState = {
  list: [{ id: 1, name: "Milk", quantity: 1, purchasePrice:1000, sellingPrice: 1230 }],

};

const AddProductForm = () => {
  
  const dispatch = useDispatch();
  const [name, setName] = useState("");
  const [purchasePrice, setPurchasePrice] = useState("");
  const [sellingPrice, setSellingPrice] = useState("");
  const [quantity, setQuantity] = useState("");

  const handleClick = (e: FormEvent) => {
    e.preventDefault();
    dispatch(createProductAction({ name, quantity: parseInt(quantity), purchasePrice: parseInt(purchasePrice), sellingPrice: parseInt(sellingPrice)}));
  };

   return (
    <div >
      <form className="block-add" onSubmit={handleClick}>
        <input
          placeholder="Имя"
          value={name}
          onChange={(e) => setName(e.target.value)}
        />
       
        <input
          placeholder="Кол-во"
          type="number"
          value={quantity}
          onChange={(e) => setQuantity(e.target.value)}
        />

        <input
          placeholder="Закупочная цена"
          value={purchasePrice}
          onChange={(e) => setPurchasePrice(e.target.value)}
        />

        <input
          placeholder="Цена продажи"
          value={sellingPrice}
          onChange={(e) => setSellingPrice(e.target.value)}
        />

        <button>Добавить</button>
      </form>
    </div>
  );
};

export default AddProductForm;
