import React from 'react'
import { useSelector } from "react-redux";
import { RootState } from "../store";
import { IProduct } from "../types/index";
import { useDispatch } from "react-redux";
import { deleteProductAction, totalProductAction } from "../actions/productsActions";
import { FC } from "react";
import { EditProduct } from "../components/index";

interface IPurchasePage {
  isLoading: boolean
}


const PurchasePage = () => {
  return (
    <div>PurchasePage</div>
  )
}

export default PurchasePage


const ProductList: FC<IProductList> = () => {
  const dispatch = useDispatch();

  const products = useSelector<RootState, IProduct[]>(
    (state) => state.products.list
  );

  return (
    <div className="block-products">
      <h1>Продукты:</h1>

      <table>
        <thead>

          <tr>
            <th>
              Имя
            </th>
            <th>
              Кол-во
            </th>
            <th>
              Сумма
            </th>
            <th>
              <div>Удалить</div>
              <div> Изменить</div>
            </th>
          </tr>
        </thead>
        <tbody >
          {products.map((item) => (

            <tr key={item.id}>
              <td>
                {item.name}
              </td>
              <td>
                {item.quantity}
              </td>
              <td>
                {item.price}
              </td>

              <td>
                <div>
                  {/* @ts-ignore */}
                  <button className="btn-mini" onClick={() => { dispatch(deleteProductAction(item.id)) }}>X</button>
                </div>
                {/* @ts-ignore */}
                <EditProduct id={item.id} />
              </td>
            </tr>
          ))}
           <tr>
            <th>
            Итого
            </th>
            <th>

            </th>
            <th>

            </th>
            <td>
            </td>
          </tr>
        </tbody>
      </table>
    </div>
  )
}

export default ProductList;
